    private void txtAddMensagemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtAddMensagemActionPerformed
        // TODO add your handling code here:
        
        if("".equals(txtMensagem.getText())){
            JOptionPane.showMessageDialog(null, "Texto v√°zio, coloque um texto antes de adcionar");
        }else{
            txaTexto.append(txtMensagem.getText() + "\n");
        }
        
        txtMensagem.setText("");
    }//GEN-LAST:event_txtAddMensagemActionPerformed

